var gulp = require('gulp');
var del = require('del');
var babel = require('gulp-babel');

gulp.task('default', function() {
    // copy component sass files to lib
    gulp.src('./components/*/style/*.scss')
        .pipe(gulp.dest('./lib'));

    // copy sass files to lib
    gulp.src('./components/styles/**/*.scss')
        .pipe(gulp.dest('./lib/styles'));

    // delete js files in css directory
    del(['./dist/css/*.js']);

    // babel component js files
    gulp.src('./components/*/*.js')
       .pipe(babel())
       .pipe(gulp.dest('./lib'));

    // babel js files
    gulp.src('./components/*.js')
       .pipe(babel())
       .pipe(gulp.dest('./lib'));
});
